import React, {Component} from 'react'
import axios from 'axios'
import ReactPaginate from 'react-paginate';
import './App.css'

export default class Userfetch2 extends Component {
    constructor(props) {
        super(props);
        this.state = {
            offset: 0,
            perPage: 5,
            currentPage: 0,
            loading : true,
            person : []
        };
        this.handlePageClick = this
            .handlePageClick
            .bind(this);
    }
    receivedData() {
        axios
            .get(`https://api.randomuser.me/?results=100`)
            .then(res => {

                const data = res.person;
                const slice = data.result.slice(this.state.offset, this.state.offset + this.state.perPage)
                const userdata = slice.map(person => <React.Fragment>
                    <tr>
                        <th scope="row">1</th>
                        <td><img className="rounded-circle" style={{width:"40px"}}
                                 src={person.picture.thumbnail}
                                 alt="activity-user" /></td>
                        <td>{person.name.first} {person.name.last}</td>
                        <td>{person.registered.date.split('T')[0]}</td>
                        <td>$250.00</td>
                        <td>{person.phone}</td>
                        <td>{person.email}</td>
                        <td>{person.location.country}</td>
                        <td>
                            <button type="button"
                                    className="btn btn-success">Verified
                            </button>
                        </td>
                        <td>
                            <button type="button" className="btn btn-primary"
                                    title="View Details" data-toggle="tooltip">View
                                Details
                            </button>
                        </td>
                    </tr>
                </React.Fragment>)

                this.setState({
                    pageCount: Math.ceil(data.length / this.state.perPage),
                    userdata
                })
            });
    }
    handlePageClick = (e) => {
        const selectedPage = e.selected;
        const offset = selectedPage * this.state.perPage;

        this.setState({
            currentPage: selectedPage,
            offset: offset
        }, () => {
            this.receivedData()
        });

    };

    componentDidMount() {
        this.receivedData()
    }
    render() {
        return (
            <div>
                {this.state.userdata}
                <ReactPaginate
                    previousLabel={"prev"}
                    nextLabel={"next"}
                    breakLabel={"..."}
                    breakClassName={"break-me"}
                    pageCount={this.state.pageCount}
                    marginPagesDisplayed={2}
                    pageRangeDisplayed={5}
                    onPageChange={this.handlePageClick}
                    containerClassName={"pagination"}
                    subContainerClassName={"pages pagination"}
                    activeClassName={"active"}/>
            </div>

        )
    }
}
